public class Pet {
    private String name;
    private String species;
    private String color;
    private boolean isAlive;
    private int age;

    public Pet(String name, String species, String color, boolean isAlive, int age) {
        this.name = name;
        this.species = species;
        this.color = color;
        this.isAlive = isAlive;
        this.age = age;
    }
}